import React, { Component } from "react";
import NavBar from "./navegation/NavBar";
import RealMap from "./map/RealMap";
import { Grid, Container, Segment } from "semantic-ui-react";

export default class RealTime extends Component {
  constructor() {
    super();
    this.state = {
      lat: 0,
      lon: 0,
      mess: "Loading"
    };
  }
  async componentDidMount() {
    await this.getLastPosition();
  }
  getLastPosition = async () => {
    const reqBody = { email: "will.canti2697@gmail.com", plate: "CKN-363" };
    const req = {
      method: "POST",
      body: JSON.stringify(reqBody),
      headers: { "Content-Type": "application/json" }
    };
    try {
      const res = await fetch("http://localhost:5000/track/api/lastPos", req);
      const coords = await res.json();
      this.setState({
        lat: coords.latitud,
        lon: coords.longitud,
        loaded: true
      });
    } catch (error) {
      this.setState({ mess: "Was not able to connect to dataBase" });
      console.log(error.message);
    }
  };
  render() {
    const { lat, lon, loaded, mess } = this.state;
    return (
      <div>
        <NavBar />
        <Grid columns={2} divided padded inverted>
          <Grid.Column width={4}>
            <Segment>
              Charts
            </Segment>
          </Grid.Column>
          <Grid.Column width={12}>
            {loaded 
            ? <Segment style={{height: "400px",     padding: "0", margin: "0",
            margin: "0"}}>
                <RealMap lat={lat} lon={lon} />
              </Segment> 
            : <div>{mess}</div>}
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
