import React, { Component } from "react";
import NavBar from "./navegation/NavBar";
import RealMap from "./map/RealMap";
import { Grid, Container, Segment } from "semantic-ui-react";
const styles = {
  text: {
    color: 'white',
  },
  mapContainer:{
    height: "500px",
    padding: "0",
    margin: "0",
  }
}

export default class RealTime extends Component {
  constructor() {
    super();
    this.state = {
      currentPos: null,
      path: [],
      mess: "Loading"
    };
  }
  componentDidMount() {
    setInterval(async () => {
      await this.getLastPosition();
    }, 5000)
  }

  getLastPosition = async () => {
    const reqBody = { email: "will.canti2697@gmail.com", plate: "CKN-363" };
    const req = {
      method: "POST",
      body: JSON.stringify(reqBody),
      headers: { "Content-Type": "application/json" }
    };
    try {
      const res = await fetch("http://192.168.1.8:5000/track/api/lastPos", req);
      const coords = await res.json();
      let actPos = { lat: this.props.lat, lng: this.props.lon };
      this.setState({
        currentPos: actPos,
        path: [...this.state.path, actPos],
        loaded: true
      });
    } catch (error) {
      this.setState({ mess: "Was not able to connect to the dataBase" });
      console.log(error.message);
    }
  };
  render() {
    const { currentPos, path, loaded, mess } = this.state;
    return (
      <div>
        <NavBar />
        <Grid stackable columns={2} divided padded inverted >
          <Grid.Column width={4} >
          <Segment color='black' inverted>
              Charts
            </Segment>  
          </Grid.Column>
          <Grid.Column width={12}>
            {loaded 
            ? <Segment color='black' inverted style={styles.mapContainer}>
                <RealMap  currentPos={currentPos} path={path} />
              </Segment> 
            : <div style={styles.text}>{mess}</div>}
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
