import PropTypes from "prop-types";
import React, { Component } from "react";
import { Menu, Responsive, Visibility } from "semantic-ui-react";
const getWidth = () => {
  const isSSR = typeof window === "undefined";
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth;
};

class DesktopNav extends Component {
  state = { activeItem: "home" };
  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  render() {
    const { activeItem } = this.state;
    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility
          once={false}
          onBottomPassed={this.showFixedMenu}
          onBottomPassedReverse={this.hideFixedMenu}
        >
          <Menu inverted pointing>
            <Menu.Item as="h1">TrackApp</Menu.Item>
            <Menu.Menu position="right">
              <Menu.Item as="p">Welcome Username</Menu.Item>
              <Menu.Item as="a" active={activeItem === "RealTime"}>
                RealTime
              </Menu.Item>
              <Menu.Item as="a" active={activeItem === "Historical"}>
                Historical
              </Menu.Item>
              <Menu.Item as="a">Log out</Menu.Item>
            </Menu.Menu>
          </Menu>
        </Visibility>
      </Responsive>
    );
  }
}

DesktopNav.propTypes = {
  children: PropTypes.node
};

export default DesktopNav;
