import React, { Component } from "react";
import NavBar from "./navegation/NavBar";
import RealMap from "./map/RealMap";
import { Grid } from "semantic-ui-react";

export default class RealTime extends Component {
  constructor() {
    super();
    this.state = {
      lat: 0,
      lon: 0
    };
  }
  async componentDidMount() {
    await this.getLastPosition();
  }
  getLastPosition = async () => {
    const reqBody = { email: "will.canti2697@gmail.com", plate: "CKN-363" };
    const req = {
      method: "POST",
      body: JSON.stringify(reqBody),
      headers: { "Content-Type": "application/json" }
    };
    try {
      const res = await fetch("http://localhost:5000/track/api/lastPos", req);
      const coords = await res.json();
      this.setState({
        lat: coords.latitud,
        lon: coords.longitud,
        loaded: true
      });
    } catch (error) {
      console.log(error.message);
    }
  };
  render() {
    const { lat, lon, loaded } = this.state;
    return (
      <div>
        <NavBar />
        <Grid>
          <Grid.Column mobile={16} largeScreen={4}>
            Charts
          </Grid.Column>
          <Grid.Column mobile={16} largeScreen={12}>
            <RealMap lat={lat} lon={lon} loaded={loaded} />
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
